/**
 * @brief 串口中断的函数
 */
void USART1_IRQHandler(void)
{
	static uint8_t RxStatus;
	static uint16_t RxIdx;
	uint8_t Temp;

    if(USART_GetITStatus(USART1, USART_IT_RXNE) == SET)
    {
        Img[RxIdx] = USART_ReceiveData(USART1);
		RxIdx++;
		if(RxIdx >= 1024)
		{
			RxIdx = 0;
			RxFlag = 1;
		}
    }
}